代码 <pre><code>#include &lt;cstring&gt;
#include &lt;iostream&gt;
#include &lt;queue&gt;
#define TO(x) edge[x].to
#define W(x) edge[x].w
#define NXT(x) edge[x].nxt
using namespace std;
typedef long long ll;
const int MAXN1 = 205, MAXN2 = 5005;
const ll INF = 1ll &lt;&lt; 50;
int n, m, s, t;
class Graph {
private:
    struct Edge {
        int to, w, nxt;
        Edge() {}
        Edge(int to, int w, int nxt) : to(to), w(w), nxt(nxt) {}
    };
    int tot, cur[MAXN1], dep[MAXN1], head[MAXN1];
    Edge edge[MAXN2 &lt;&lt; 1];
    bool bfs() {
        memset(dep, 0, sizeof(dep)), memcpy(cur, head, sizeof(cur));
        queue&lt;int&gt; q;
        for (dep[s] = 1, q.emplace(s); !q.empty(); q.pop())
            for (int e = head[q.front()]; e; e = NXT(e))
                if (W(e) &amp;&amp; !dep[TO(e)]) {
                    dep[TO(e)] = dep[q.front()] + 1, q.emplace(TO(e));
                    if (TO(e) == t)
                        return true;
                }
        return false;
    }
    ll dfs(int x, ll flow) {
        if (x == t)
            return flow;
        ll now = flow;
        for (int &amp;e = cur[x]; e; e = NXT(e))
            if (W(e) &amp;&amp; dep[TO(e)] == dep[x] + 1) {
                int f = dfs(TO(e), min(now, 1ll * W(e)));
                W(e) -= f, W(e ^ 1) += f, now -= f;
                if (!now)
                    break;
            }
        return flow - now;
    }

public:
    Graph() { tot = 1; }
    void addEdge(int u, int v, int w) {
        edge[++tot] = Edge(v, w, head[u]), head[u] = tot;
        edge[++tot] = Edge(u, 0, head[v]), head[v] = tot;
    }
    ll solve() {
        ll ans = 0;
        while (bfs())
            ans += dfs(s, INF);
        return ans;
    }
} graph;
int main() {
    ios::sync_with_stdio(false), cin.tie(0);
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; s &gt;&gt; t;
    for (int i = 1, u, v, w; i &lt;= m; ++i)
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; w, graph.addEdge(u, v, w);
    cout &lt;&lt; graph.solve() &lt;&lt; "\n";
}
</code></pre>

代码 <pre><code>#include &lt;cstring&gt;
#include &lt;iostream&gt;
#include &lt;queue&gt;
#define TO(x) edge[x].to
#define W(x) edge[x].w
#define C(x) edge[x].c
#define NXT(x) edge[x].nxt
using namespace std;
typedef pair&lt;int, int&gt; pii;
const int MAXN1 = 5005, MAXN2 = 5e4 + 5, INF = (1ll &lt;&lt; 31) - 1;
int n, m, s, t;
class Graph {
private:
    struct Edge {
        int to, w, c, nxt;
        Edge() {}
        Edge(int to, int w, int c, int nxt) : to(to), w(w), c(c), nxt(nxt) {}
    };
    int tot, cost, dis[MAXN1], cur[MAXN1], head[MAXN1];
    bool vis[MAXN1];
    Edge edge[MAXN2 &lt;&lt; 1];
    bool spfa() {
        memcpy(cur, head, sizeof(cur));
        queue&lt;int&gt; q;
        for (int i = 1; i &lt;= n; ++i)
            dis[i] = INF;
        for (dis[s] = 0, q.emplace(s); !q.empty(); vis[q.front()] = false, q.pop())
            for (int e = head[q.front()]; e; e = NXT(e))
                if (W(e) &amp;&amp; dis[TO(e)] &gt; dis[q.front()] + C(e)) {
                    dis[TO(e)] = dis[q.front()] + C(e);
                    if (!vis[TO(e)])
                        q.emplace(TO(e)), vis[TO(e)] = true;
                }
        return dis[t] &lt; INF;
    }
    int dfs(int x, int flow) {
        if (x == t)
            return flow;
        vis[x] = true;
        int now = flow;
        for (int &amp;e = cur[x]; e; e = NXT(e))
            if (W(e) &amp;&amp; !vis[TO(e)] &amp;&amp; dis[TO(e)] == dis[x] + C(e)) {
                int f = dfs(TO(e), min(W(e), now));
                W(e) -= f, W(e ^ 1) += f, now -= f, cost += f * C(e);
                if (!now)
                    break;
            }
        return vis[x] = false, flow - now;
    }

public:
    Graph() { tot = 1; }
    void addEdge(int u, int v, int w, int c) {
        edge[++tot] = Edge(v, w, c, head[u]), head[u] = tot;
        edge[++tot] = Edge(u, 0, -c, head[v]), head[v] = tot;
    }
    pii solve() {
        cost = 0;
        int ans = 0;
        while (spfa())
            ans += dfs(s, INF);
        return {ans, cost};
    }
} graph;
int main() {
    ios::sync_with_stdio(false), cin.tie(0);
    cin &gt;&gt; n &gt;&gt; m &gt;&gt; s &gt;&gt; t;
    for (int i = 1, u, v, w, c; i &lt;= m; ++i)
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; w &gt;&gt; c, graph.addEdge(u, v, w, c);
    pii ans = graph.solve();
    cout &lt;&lt; ans.first &lt;&lt; " " &lt;&lt; ans.second &lt;&lt; "\n";
}
</code></pre>
